import './UserForm.css';
import Card from '../Card'
import '../Card.css'
import { useState } from 'react';
import Alert from 'react-popup-alert'

<link rel="stylesheet" href="https://www.w3schools.com/w3css/4/w3.css"></link>

export default function UserForm(props) {

    let userData;

    let [enteredAge, setEnteredAge] = useState('');
    let [enteredName, setEnteredName] = useState('');

    const ageChangeHandler = (event) => {
        setEnteredAge(event.target.value);
    }

    const nameChangeHandler = (event) => {
        setEnteredName(event.target.value);
    }

    const onSubmitHandler = (event) => {
        event.preventDefault();

        setEnteredAge('');
        setEnteredName('');

        userData = {
            id: Math.random().toString(),
            name: enteredName,
            age: enteredAge
        }    
        
        onShowAlert('error');
        
        // console.log(userData)
        props.onReceive(userData);
    }

    const [alert, setAlert] = useState({
        type: 'error',
        text: 'This is a alert message',
        show: false
      })
    
      function onCloseAlert() {
        setAlert({
          type: '',
          text: '',
          show: false
        })
      }
    
      function onShowAlert(type) {
        setAlert({
          type: type,
          text: 'Demo alert',
          show: true
        })
      }

    return (
        <form>
            <Card>
                <div className="new-user__control">
                    <label>Username</label>
                    <input type="text" onChange={nameChangeHandler} value={enteredName}></input>
                </div>
                <div className="new-user__control">
                    <label>Age</label>
                    <input type="number" max={999} onChange={ageChangeHandler} value={enteredAge}></input>
                </div>
                <div className="new-user__actions">
                    <button type="submit" className="bn3637 bn38" onClick={onSubmitHandler}><i>Add User</i></button>
                </div>
            </Card>
            <div>
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: 50 }}>
        <button onClick={() => onCloseAlert()}>Hide alert</button>
        <button onClick={() => onShowAlert('success')}>
          Show success alert
        </button>
        <button onClick={() => onShowAlert('error')}>Show error alert</button>
        <button onClick={() => onShowAlert('warning')}>
          Show warning alert
        </button>
      </div>
      <Alert
        header={'Header'}
        btnText={'Close'}
        text={alert.text}
        type={alert.type}
        show={alert.show}
        onClosePress={onCloseAlert}
        pressCloseOnOutsideClick={true}
        showBorderBottom={true}
        alertStyles={{}}
        headerStyles={{}}
        textStyles={{}}
        buttonStyles={{}}
      />
    </div>
        </form>

    )

};
