import './UserForm.css';

import Card from '../Card'
import '../Card.css'
import { useState } from 'react';

<link rel="stylesheet" href="https://www.w3schools.com/w3css/4/w3.css"></link>

export default function UserForm(props) {

    let userData;

    let [enteredAge, setEnteredAge] = useState('');
    let [enteredName, setEnteredName] = useState('');

    const ageChangeHandler = (event) => {
        setEnteredAge(event.target.value);
    }

    const nameChangeHandler = (event) => {
        setEnteredName(event.target.value);
    }

    const onSubmitHandler = (event) => {
        event.preventDefault();

        

        userData = {
            id: Math.random().toString(),
            name: enteredName,
            age: enteredAge
        }    

        console.log(userData)
    }



    return (
        <form>
            <Card>
                <div className="new-user__control">
                    <label>Username</label>
                    <input type="text" onChange={nameChangeHandler} value={enteredName}></input>
                </div>
                <div className="new-user__control">
                    <label>Age</label>
                    <input type="number" max={999} onChange={ageChangeHandler} value={enteredAge}></input>
                </div>
                <div className="new-user__actions">
                    <button type="submit" className="bn3637 bn38" onClick={onSubmitHandler}><i>Add Expense</i></button>
                </div>
            </Card>
        </form>

    )

};
